let player;

export function setPlayer(container: HTMLElement) {
    if (player) {
        return
    }
    player = new Aplayer({
        container,
        mini: false,
        autoplay: false,
        loop: false,
        // mutex: false,
        listFolded: true,
        // listMaxHeight: 90,
    });
    setAudio(data.audio[parseInt(store.id)-1]);
}

export function destroyPlayer() {
    if (player) {
        player.destroy();
        player = null;
    }
}

export function setAudio(audio) {
    player.list.clear();
    player.list.add(audio);
}

export function isNotInt(id){
    return /\D/.test(id)||parseInt(id)<1||parseInt(id)>300
}

// const urls=new Set();
/**
 * 请求
 * @param url
 * @param options
 */
export function xhttp(url: string, options?: RequestInit){
    const defaultOptions = <RequestInit>{credentials: "include"};
    url =`${url}`;
    options = options ? Object.assign({}, defaultOptions, options) : defaultOptions;
    let request = new Request(url, options);

    let promise = fetch(request);

    // urls.add(request);

    return promise.then((response) => {
        // urls.delete(request);
        if(response.ok){
            return response.text();
        }
        throw new Error(response.statusText);
    },(e)=>{
        // urls.delete(request);
        throw e;
    });
}

/**
 * 处理请求错误
 * @param e
 */
export function handleFetchError(e) {
    console.log(e);
    alert(e.toString());
}

